import {
  mapState
} from 'vuex'
export default {
  data () {
    return {
      // 站点启事统计信息
      homeInfo: {
        noticeSum: '',
        userSum: ''
      },
      // 服务器状态信息
      ECSInfo: {
        freemem:'',
        totalmem:'',
        usedmemBL:'',
        usedmemBLM:'',
        cpu:''
      },
      dailySalesChart: {
        data: {
          labels: ['M', 'T', 'W', 'T', 'F', 'S', 'S'],
          series: [
            [12, 17, 7, 17, 23, 18, 38]
          ]
        },
        options: {
          lineSmooth: this.$chartist.Interpolation.cardinal({
            tension: 0
          }),
          low: 0,
          high: 50,
          chartPadding: {
            top: 0,
            right: 0,
            bottom: 0,
            left: 0
          }
        }
      },
      dataCompletedTasksChart: {
        data: {
          labels: ['12am', '3pm', '6pm', '9pm', '12pm', '3am', '6am', '9am'],
          series: [
            [230, 750, 450, 300, 280, 240, 200, 190]
          ]
        },
        options: {
          lineSmooth: this.$chartist.Interpolation.cardinal({
            tension: 0
          }),
          low: 0,
          high: 1000, // creative tim: we recommend you to set the high sa the biggest value + something for a better look
          chartPadding: {
            top: 0,
            right: 0,
            bottom: 0,
            left: 0
          }
        }
      },
      emailsSubscriptionChart: {
        data: {
          labels: ['Ja', 'Fe', 'Ma', 'Ap', 'Mai', 'Ju', 'Jul', 'Au', 'Se', 'Oc', 'No', 'De'],
          series: [
            [542, 443, 320, 780, 553, 453, 326, 434, 568, 610, 756, 895]

          ]
        },
        options: {
          axisX: {
            showGrid: false
          },
          low: 0,
          high: 1000,
          chartPadding: {
            top: 0,
            right: 5,
            bottom: 0,
            left: 0
          }
        },
        responsiveOptions: [
          ['screen and (max-width: 640px)', {
            seriesBarDistance: 5,
            axisX: {
              labelInterpolationFnc: function (value) {
                return value[0]
              }
            }
          }]
        ]
      },
      tabs: 0,
      list: {
        0: false,
        1: false,
        2: false
      }
    }
  },
  computed: {
    ...mapState({
      login: state => state.login
    })
  },
  methods: {
    complete (index) {
      this.list[index] = !this.list[index]
    }
  },
  created () {
    var that = this
    that.axios.post('/api/getHomeInfo')
      .then(function (res) {
        that.homeInfo.userSum = res.data[1][0].userSum
        that.homeInfo.noticeSum = res.data[0][0].noticeSum
        console.log(that.homeInfo.userSum,that.homeInfo.noticeSum)
      }).catch(function (res) {
      console.log(res)
    })
    that.axios.post('/api/getECSInfo')
      .then(function (res) {
        that.ECSInfo = res.data
        console.log(that.ECSInfo)
      }).catch(function (res) {
        console.log(res)
      })
    if (this.login) {
    } else {
      this.$router.push('/admin')
    }
  }
}
