import {
  mapState
} from 'vuex'
export default {
  data: () => ({
    // 用于列表 分页的page
    page: 1,
    // 用户列表 表格头部
    headers: [
      {
        sortable: false,
        text: 'ID',
        value: 'userID'
      },
      {
        sortable: false,
        text: '姓名',
        value: 'realName'
      },
      {
        sortable: false,
        text: '学号',
        value: 'userNo'
      },
      {
        sortable: false,
        text: '昵称',
        value: 'userName'
      },
      {
        sortable: false,
        text: '电话',
        value: 'phone'
      },
      {
        sortable: false,
        text: '邮箱',
        value: 'email'
      },
      {
        sortable: false,
        text: '密码',
        value: 'password'
      },
      {
        sortable: false,
        text: '状态',
        value: 'status',
        align: 'right'
      }
    ],
    // 本页面的 用户信息列表
    userInfoList: [],
    // 本页面 当前分页的用户信息列表
    userInfoListPage: [],
    // 用户状态选项列表
    statusList: [
      { value: '000', status: '正常', color: 'green' },
      { value: '100', status: '警告', color: 'orange' },
      { value: '200', status: '已封', color: 'red' }
    ],
    // 搜索的用户关键词
    sUserInfo: '',
  }),
  methods: {
    test () {
      console.log('hh')
    },
    listPage (page) {
      this.userInfoListPage = []
      console.log(page)
      var start = page*20 - 20
      var end = start + 20
      for( var i = start; i < end; i++){
        if( this.userInfoList[i] != undefined ){
          this.userInfoListPage.push(this.userInfoList[i])
        } else {
          break
        }
      }
      console.log(this.userInfoListPage)
    },
    // 改变指定用户的状态
    changeUserStatus (userID, status) {
      console.log(userID, status)
      var that = this
      that.axios
        .post('/api/changeUserStatus', {
          userID,
          status
        })
        .then(function (res) {
          if (res.data === '0000') {
            that.$router.push('/u-refresh')
          }
        })
        .catch(function (res) {
          console.log(res)
        })
    },
    // 得到用户信息列表
    getSearchUserInfo (sUserInfo) {
      var that = this
      // 获取用户信息并存储在本地仓库
      this.axios
        .post('/api/getSearchUserInfo', {
          sUserInfo
        }).then(function (res) {
          that.$store.state.userInfoList = res.data
          that.userInfoList = res.data
          that.maxPage = Math.ceil(res.data.length/20)
          // 再次搜索请求时，将原有的清空
          that.userInfoListPage = []
          for( var i = 0; i < 20; i++){
            if( that.userInfoList[i] != undefined ){
              that.userInfoListPage.push(that.userInfoList[i])
            } else {
              break
            }
          }
          console.log(that.userInfoListPage)
        })
        .catch(function (res) {
          console.log(res)
        })
    }
  },
  computed: {
    ...mapState({
      login: state => state.login
    })
  },
  created () {
    if (this.login) {
      var that = this
      that.getSearchUserInfo(that.sUserInfo)
    } else {
      this.$router.push('/admin')
    }
  }
}
